(function() {
  var SearchDescription;

  SearchDescription = (function() {
    SearchDescription.prototype.ranking = {
      'full': 1,
      'partial': 2,
      'none': 3
    };

    function SearchDescription(hit, options) {
      this.hit = hit;
      this.customTitle = null;
      this.customDescription = null;
      if (options) {
        this.customTitle = options.title;
        this.customDescription = options.description;
      }
    }

    SearchDescription.prototype.matches = function() {
      var results;
      return results = _.reject(this.hit._highlightResult, function(hit, key) {
        return hit.matchLevel === 'none' || /name/i.test(key);
      });
    };

    SearchDescription.prototype.title = function() {
      var result;
      result = this.hit._highlightResult;
      if (result[this.customTitle]) {
        return result[this.customTitle].value;
      } else if (result.full_name) {
        return result.full_name.value;
      } else if (result.name) {
        return result.name.value;
      }
    };

    SearchDescription.prototype.description = function() {
      var result;
      result = this.orderHits()[0];
      try {
        if (result[this.customDescription]) {
          result[this.customDescription].value;
        }
      } catch (undefined) {}
      if (result) {
        return result.value;
      } else if (this.hit.city) {
        return "City: " + this.hit.city;
      }
    };

    SearchDescription.prototype.orderHits = function() {
      return _.sortBy(this.matches(), (function(hit) {
        return this.ranking[hit.matchLevel];
      }), this);
    };

    SearchDescription.prototype.toHash = function() {
      var description;
      description = this.description();
      if (description && description.length > 20) {
        description = description.substring(0, 20) + '...';
      }
      return {
        title: this.title(),
        description: description
      };
    };

    return SearchDescription;

  })();

  this.SearchDescription = SearchDescription;

}).call(this);
$(function() {
  var client = algoliasearch("", ""),
      searchField = $('#global-search'),
      searchResults = $('#search-results'),

      postResults = createPostsResultsBox('board-posts', 'Board Posts'),
      userResults = createResultsBox('czars', 'Czars'),
      eventResults = createResultsBox('events', 'Events'),
      groupResults = createResultsBox('groups', 'Groups'),
      clientResults = createResultsBox('clients', 'Clients');

  $('#close-search').on('click', function(e) {
    e.preventDefault();
    $('body').removeClass('search-active');
    searchField.val('');
  });


  function createResultsBox(id, title) {
    var content = $('<div />', {
      "class": 'col-md-3 result-listings',
      id: id,
      html: '<div class="panel panel-default"><div class="panel-heading">' + title + '</div><div class="panel-body"><ul id="results" class="search-result-listing"></ul></div></div>'
    }).appendTo(searchResults).hide();

    return content;
  }

  function createPostsResultsBox(id, title) {
    var content = $('<div />', {
      "class": 'col-md-6 result-listings',
      id: id,
      html: '<div class="panel panel-default"><div class="panel-heading">' + title + '</div><div class="panel-body"><div class="row-fluid"><ul class="col-md-4 search-result-listing" id="results-top-users"></div><div class="col-md-8" id="results"></div></div></div></div>'
    }).appendTo(searchResults).hide();

    return content;
  }

  var defaultResultsMarkup = (function() {
    var template = Hogan.compile('<li><a class="search-result" href="{{ slug }}">{{#image}}<img class="result-image user-avatar" src="{{ image }}" />{{/image}}<span class="result-title">{{ title }}</span><span class="result-detail">{{{ text }}}</span></li>');

    return function(result) {
      return template.render(result);
    }
  })();

  var topUsersMarkup = (function() {
    var template = Hogan.compile('<li><a class="search-result" href="{{ user.slug }}">{{#user.avatar}}<img class="result-image user-avatar" src="{{ user.avatar }}" />{{/user.avatar}}<span class="result-title">{{ user.full_name }}</span></li>');

    return function(result) {
      return template.render(result);
    }
  })();

  function showResults(el, results, options) {
    var resultsHTML = '';
    var resultsTarget = el.find('#results');
    resultsTarget.empty();

    if( results.hits.length == 0 ) {
      el.hide()
      return;
    }

    for (var i = 0; i < results.hits.length; ++i) {
      var rawResult = results.hits[i],
          description = rawResult[options.description],
          container = $('<div />', { html: description });

          result = {
            objectID: rawResult.objectID,
            slug: rawResult.slug_url,
            image: rawResult.slug_url + '/image',
            title: rawResult[options.title],
            description: container.text().substring(0, 100),
            text: container.text().substring(0, 100),
            raw: rawResult
          };

      if( rawResult._highlightResult[options.description] ) {
        result.description = rawResult._highlightResult[options.description].value.substring(0, 100);
      }

      if( options.markup ) {
        resultsHTML += options.markup(result);
      }else {
        resultsHTML += defaultResultsMarkup(result);
      }
    }

    resultsTarget.append(resultsHTML);
    el.show();
  }

  function showTopUserResults(el, results) {
    var resultsHTML = '',
        resultsTarget = el.find('#results-top-users'),
        userIds = _.keys(results.facets['user.id']);

    $.getJSON('/users/blurbs', { ids: userIds }, function(response) {
      _.each(userIds, function(id) {
        var user = response[id];
        resultsHTML += topUsersMarkup({
          user: user,
          facet_count: results.facets['user.id'][id]
        });
      });

      resultsTarget.html('<h4>Active CZARS</h4>' + resultsHTML);
    });
  }

  function searchMultiCallback(error, content) {
    if (error == null) {
      var users = content.results[0];

      showResults(userResults, content.results[0], {
        title: 'full_name',
        description: 'city',
      })

      showResults(clientResults, content.results[1], {
        title: 'name',
        description: 'user_full_name',
      })

      showResults(groupResults, content.results[2], {
        title: 'name',
        description: '',
      })

      showResults(eventResults, content.results[3], {
        title: 'name',
        description: 'description',
      })

      showTopUserResults(postResults, content.results[4]);

      showResults(postResults, content.results[4], {
        title: 'topic_subject',
        description: 'text',
        markup: (function() {
          var template = Hogan.compile('<div class="result"><h4><a href="{{ slug }}">{{{ title }}}</a></h4><p class="meta"><em>{{ raw.user.full_name }}</em> - {{ createdAtFormatted }}</p><p class="description">{{{ description }}}</p></div>');

          return function(result) {
            var createdAt = new moment.unix(result.raw.created_at);
            result.createdAtFormatted = createdAt.format('MMM D, YYYY @ H:mma');

            return template.render(result);
          }
        })()
      })
    }
  }

  $('.search-form').on('submit', function(e) {
    e.preventDefault();
    e.stopPropagation();
    return false;
  });

  searchField.on("keydown", function(e) {
    if (e.keyCode == 13) {
      e.preventDefault();
    }
  });
  searchField.on('keyup', _.throttle(function(e) {
    e.preventDefault();
    e.stopPropagation();
    var query = searchField.val();

    if( query == '') {
      return;
    }

    $('body').addClass('search-active');

    var searchQuery = [];

    
      searchQuery.push({
        indexName: "User_development",
        query: query,
        params: {
          hitsPerPage: 5
        }
      });
    
      searchQuery.push({
        indexName: "UserClient_development",
        query: query,
        params: {
          hitsPerPage: 5
        }
      });
    
      searchQuery.push({
        indexName: "Group_development",
        query: query,
        params: {
          hitsPerPage: 5
        }
      });
    
      searchQuery.push({
        indexName: "Event_development",
        query: query,
        params: {
          hitsPerPage: 5
        }
      });
    
    searchQuery.push({
      indexName: "Forem_Post_development",
      query: query,
      params: {
        hitsPerPage: 5,
        facets: 'user.id',
        maxValuesPerFacet: 5
      }
    });
    client.search(searchQuery, searchMultiCallback);

    if (e.keyCode == 13) {
      return false;
    }
  }, 200));
});

$(document).ready(function() {
  // Replace the following values by your ApplicationID and ApiKey.
  var algolia = algoliasearch("", "");
  // replace YourTeamsIndexName & YourPlayersIndexName by the name of the indexes you want to query.
  var teams = algolia.initIndex('User_development');
  var players = algolia.initIndex('UserClient_development');
  var groups = algolia.initIndex('Group_development');
  var events = algolia.initIndex('Event_development');
  var posts = algolia.initIndex('Forem_Post_development');

  var slugPaths = [
    'posts',
    'czar',
    'czar',
    'groups',
    'events'
  ];

  // Mustache templating by Hogan.js (http://mustache.github.io/)
  var templateSuggestion = Hogan.compile('<div class="suggestion">' +
    '<div class="name">{{{ title }}}</div>' +
    '{{#description}}<div class="description">{{{ description }}}</div>{{/description}}' +
  '</div>');

  function searchSource(object, perPage, title, options) {
    return {
      source: object.ttAdapter({ hitsPerPage: perPage }),
      displayKey: 'name',
      templates: {
        header: '<div class="category">' + title + '</div>',
        suggestion: function(hit) {
          var search = new SearchDescription(hit, options);
          return templateSuggestion.render(search.toHash());
        }
      }
    }
  }

  // typeahead.js initialization
  $('#member-search').typeahead({
    minLength: 3,
    autoselect: true
  }, [
    searchSource(posts, 3, 'Board Posts', { title: 'topic_subject', description: 'text' }),
    searchSource(teams, 3, 'Members'),
    searchSource(players, 3, 'Clients'),
    searchSource(groups, 3, 'Groups'),
    searchSource(events, 3, 'Events')
  ]);

  $('#member-search').on('typeahead:selected', function(e, suggestion, dataset) {
    window.location = suggestion.slug_url;
  });
});







